// Convection-Diffusion Equation

Info<< "\nCalculating Ionic Transport " << endl;

fvScalarMatrix ConvectionEqn
(
          fvm::ddt(epsf,C) 
        + fvm::div(phi,C) 
        - fvm::laplacian((d*epsf),C)
	+ fvm::div(phic,C) //correction term from clay movement
);

ConvectionEqn.solve();

//minimum value (even though it uses max function)
C.max(1e-20); 
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    


