// Convection-Diffusion Equation //

Info<< "\nCalculating Scalar Transport " << endl;

fvScalarMatrix ConvectionEqn //(C=Concentration=rho*wf/mw)
(
         fvm::ddt(epsf,C) 
        + fvm::div(phi,C) 
        - fvm::laplacian((Dion*epsf),C)
        //+ fvm::div(phic,C) //solid movement correction
);

ConvectionEqn.solve();
Cepsf = C*epsf;
C.max(0.); 
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                    


