// Convection-Diffusion Equation

Info<< "\nCalculating Ionic Transport " << endl;

fvScalarMatrix ConvectionEqn //(C=Concentration=rho*wf/mw)
(
         fvm::ddt(eps,C) 
        + fvm::div(phi,C) 
        - fvm::laplacian((D*eps),C)
);

ConvectionEqn.solve();

//minimum value (even though it uses max function)
C.max(0); 
